const dataCache = new Map();

export default {
    /**
     * 保存对象
     */
    push: function (key, vaule, isNative = false) {
        dataCache.set(key, vaule);
        isNative && store.set(key, vaule);
    },

    /**
     * 获取对象
     * @returns {Array}
     */
    get: function (key) {
        return dataCache.get(key) || store.get(key);
    }

};