import BaseHandler from "../../handler/BaseHandler";


export default class extends BaseHandler {

    constructor(options) {
        super(options);
        this._name = options.name;
        this._order = options.order;
    }

    /**
     * 生成html，请在子类中重载此方法
     */
    generateHtml() {
        return "<span>test</span>";
    }

    /**
     * 渲染完成后对html逻辑处理,根据需求决定是否需要重载
     * @param container 导航栏容器
     */
    disposeHtml(container) {
    }


    get name() {
        return this._name;
    }

    set name(value) {
        this._name = value;
    }

    get order() {
        return this._order;
    }

    set order(value) {
        this._order = value;
    }
};