import BaseNav from "../../BaseNav";

export default class extends BaseNav {
    constructor(options) {
        super(Object.assign({
            name: "nav/mail",
            order: 40
        }, options));
    }

    resolveTpl(tpl) {
        return require("./tpl/" + tpl + ".html");
    }

    /**
     * 生成html
     */
    generateHtml() {
        return this.resolveTpl("index")({});
    }


};