import BaseHandler from "../../handler/BaseHandler";

export default class extends BaseHandler {
    resolveTpl(tpl) {
        return require("./tpl/" + tpl + ".html");
    }

    encode(){
        let url=$("#urlPath").val();
        if(url){
            $("#urlCode").val(encodeURIComponent(url))
        }
    }
};

