import BaseNav from "ezdev-pcview/layout/navbar/BaseNav";

export default class extends BaseNav {
    constructor(options) {
        super(Object.assign({
            name: "nav/logout",
            order: 10
        }, options));
    }

    resolveTpl(tpl) {
        return require("./tpl/" + tpl + ".html");
    }

    /**
     * 生成html
     */
    generateHtml() {
        return this.resolveTpl("index")({});
    }

    /**
     * 退出系统
     */
    logout(){
        store.set("logout", "true");
        window.location.href="system/logout";
    }


};